pragma solidity ^0.8.20;

// OpenZeppelin v4.9.5 — стабильный импорт для Remix/Hardhat
import "@openzeppelin/contracts@4.9.5/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts@4.9.5/token/ERC20/extensions/ERC20Burnable.sol";

contract GADCoin is ERC20, ERC20Burnable {
    // 10_000_000_000_000 * 10^18 (18 знаков после запятой как в стандартном ERC-20)
    uint256 public constant MAX_SUPPLY = 10_000_000_000_000 * 10 ** 18;

    // initialRecipient: казначейский адрес, куда зайдет весь выпуск.
    // Если передать address(0), получателем станет deployer (msg.sender).
    constructor(address initialRecipient) ERC20("GAD coin", "GAD") {
        address recipient = (initialRecipient == address(0)) ? msg.sender : initialRecipient;
        _mint(recipient, MAX_SUPPLY);
    }
