pragma solidity ^0.8.20;

// OpenZeppelin 4.9.5
import "@openzeppelin/contracts@4.9.5/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts@4.9.5/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts@4.9.5/token/ERC20/IERC20.sol";

/// @notice Простая «копилка-замок»: держит токены до releaseTime, потом бенефициар вызывает release()
contract GADLockbox {
    IERC20  public immutable token;
    address public immutable beneficiary;
    uint256 public immutable releaseTime;
    bool    public released;

    event Released(address indexed to, uint256 amount);

    constructor(IERC20 _token, address _beneficiary, uint256 _releaseTime) {
        require(address(_token) != address(0), "token=0");
        require(_beneficiary != address(0), "beneficiary=0");
        require(_releaseTime > block.timestamp, "release in past");
        token = _token;
        beneficiary = _beneficiary;
        releaseTime = _releaseTime;
    }

    function release() external {
        require(block.timestamp >= releaseTime, "still locked");
        require(!released, "already released");
        released = true;
        uint256 bal = token.balanceOf(address(this));
        require(bal > 0, "no tokens");
        require(token.transfer(beneficiary, bal), "transfer failed");
        emit Released(beneficiary, bal);
    }
}

/// @notice GAD coin: 10 трлн фикс, 50% сразу, 50% в замке на 6 мес. Сжигание включено.
contract GADCoin is ERC20, ERC20Burnable {
    // 10_000_000_000_000 * 10^18
    uint256 public constant MAX_SUPPLY = 10_000_000_000_000 * 10 ** 18;

    // адрес созданного замка (для прозрачности)
    GADLockbox public immutable lockbox;

    event Locked(address indexed lockbox, uint256 amount, uint256 releaseTime);

    /// @param immediateRecipient кто сразу получает 50% (если 0 — это deployer)
    /// @param lockBeneficiary кто получит вторые 50% после разблокировки (если 0 — immediateRecipient)
    constructor(address immediateRecipient, address lockBeneficiary)
        ERC20("GAD coin", "GAD")
    {
        address liquid = (immediateRecipient == address(0)) ? msg.sender : immediateRecipient;
        address bene   = (lockBeneficiary   == address(0)) ? liquid       : lockBeneficiary;

        // создаём «замок» с релизом через 180 дней
        GADLockbox _lock = new GADLockbox(IERC20(address(this)), bene, block.timestamp + 180 days);
        lockbox = _lock;

        // минтим 50% сразу и 50% в замок
        uint256 half = MAX_SUPPLY / 2;
        _mint(liquid, half);
        _mint(address(_lock), MAX_SUPPLY - half);

        emit Locked(address(_lock), MAX_SUPPLY - half, _lock.releaseTime());
    }
}

