// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts@4.9.5/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts@4.9.5/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts@4.9.5/token/ERC20/extensions/ERC20Permit.sol";
import "@openzeppelin/contracts@4.9.5/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts@4.9.5/access/Ownable.sol";
import "@openzeppelin/contracts@4.9.5/security/Pausable.sol";

contract GADCoinV2 is ERC20, ERC20Burnable, ERC20Permit, Ownable, Pausable {
    // 10_000_000_000_000 * 10^18
    uint256 public constant MAX_SUPPLY = 10_000_000_000_000 * 10 ** 18;

    mapping(address => bool) public isBlacklisted;
    uint256 public maxTxAmount;       // 0 = –æ—Ç–∫–ª—é—á–µ–Ω–æ
    uint256 public maxWalletAmount;   // 0 = –æ—Ç–∫–ª—é—á–µ–Ω–æ
    bool    public tradingEnabled;    // –¥–æ –≤–∫–ª—é—á–µ–Ω–∏—è ‚Äî —Ç–æ—Ä–≥–æ–≤–∞—Ç—å –Ω–µ–ª—å–∑—è (–∫—Ä–æ–º–µ owner)

    event BlacklistUpdated(address indexed account, bool isBlacklisted);
    event MaxTxAmountUpdated(uint256 amount);
    event MaxWalletAmountUpdated(uint256 amount);
    event TradingEnabled(bool enabled);

    constructor(address initialRecipient, address initialOwner)
        ERC20("GAD coin", "GAD")
        ERC20Permit("GAD coin")
        Ownable()
    {
        address recipient = (initialRecipient == address(0)) ? _msgSender() : initialRecipient;
        _mint(recipient, MAX_SUPPLY);

        // –ü–µ—Ä–µ–Ω–æ—Å–∏–º –≤–ª–∞–¥–µ–Ω–∏–µ, –µ—Å–ª–∏ —É–∫–∞–∑–∞–Ω –¥—Ä—É–≥–æ–π –≤–ª–∞–¥–µ–ª–µ—Ü (–Ω–∞–ø—Ä–∏–º–µ—Ä, –º—É–ª—å—Ç–∏—Å–∏–≥ Safe)
        if (initialOwner != address(0) && initialOwner != _msgSender()) {
            _transferOwnership(initialOwner);
        }

        tradingEnabled = false; // –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é –≤—ã–∫–ª—é—á–µ–Ω–æ
    }

    // --- –ê–¥–º–∏–Ω-—Ñ—É–Ω–∫—Ü–∏–∏ ---
    function setBlacklist(address account, bool value) external onlyOwner {
        isBlacklisted[account] = value;
        emit BlacklistUpdated(account, value);
    }

    function setMaxTxAmount(uint256 amount) external onlyOwner {
        maxTxAmount = amount;
        emit MaxTxAmountUpdated(amount);
    }

    function setMaxWalletAmount(uint256 amount) external onlyOwner {
        maxWalletAmount = amount;
        emit MaxWalletAmountUpdated(amount);
    }

    function setTradingEnabled(bool value) external onlyOwner {
        tradingEnabled = value;
        emit TradingEnabled(value);
    }

    function pause() external onlyOwner { _pause(); }
    function unpause() external onlyOwner { _unpause(); }

    // –†–µ—Å–∫—å—é –æ—à–∏–±–æ—á–Ω–æ –ø—Ä–∏—Å–ª–∞–Ω–Ω—ã—Ö —Å—Ä–µ–¥—Å—Ç–≤
    function rescueERC20(address token, uint256 amount, address to) external onlyOwner {
        require(to != address(0), "to=0");
        IERC20(token).transfer(to, amount);
    }

    function rescueNative(address payable to, uint256 amount) external onlyOwner {
        require(to != address(0), "to=0");
        (bool ok,) = to.call{value: amount}("");
        require(ok, "transfer failed");
    }

    // –ï–¥–∏–Ω–∞—è —Ç–æ—á–∫–∞ –ø—Ä–æ–≤–µ—Ä–æ–∫ –¥–ª—è –ø–µ—Ä–µ–≤–æ–¥–æ–≤/–º–∏–Ω—Ç–∞/—Å–∂–∏–≥–∞–Ω–∏—è
    function _update(address from, address to, uint256 value) internal override {
        require(!paused(), "Token paused");

        if (from != address(0)) require(!isBlacklisted[from], "Sender blacklisted");
        if (to   != address(0)) require(!isBlacklisted[to],   "Recipient blacklisted");

        // –î–æ –≤–∫–ª—é—á–µ–Ω–∏—è —Ç–æ—Ä–≥–æ–≤–ª–∏: —Ä–∞–∑—Ä–µ—à–µ–Ω—ã —Ç–æ–ª—å–∫–æ –æ–ø–µ—Ä–∞—Ü–∏–∏ —Å —É—á–∞—Å—Ç–∏–µ–º –≤–ª–∞–¥–µ–ª—å—Ü–∞ (–º–∏–Ω—Ç –±—ã–ª –≤ –∫–æ–Ω—Å—Ç—Ä—É–∫—Ç–æ—Ä–µ)
        if (!tradingEnabled) {
            if (from != address(0) && to != address(0)) {
                require(from == owner() || to == owner(), "Trading disabled");
            }
        }

        // –õ–∏–º–∏—Ç –Ω–∞ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏—é (–∫—Ä–æ–º–µ owner –∏ –º–∏–Ω—Ç/–±—ë—Ä–Ω)
        if (maxTxAmount > 0 && from != address(0) && to != address(0)) {
            if (from != owner() && to != owner()) {
                require(value <= maxTxAmount, "Exceeds maxTxAmount");
            }
        }

        // –õ–∏–º–∏—Ç –Ω–∞ –∫–æ—à–µ–ª—ë–∫ –ø–æ –ø–æ–ª—É—á–µ–Ω–∏—é (–∫—Ä–æ–º–µ owner –∏ –Ω—É–ª–µ–≤–æ–≥–æ –∞–¥—Ä–µ—Å–∞)
        if (maxWalletAmount > 0 && to != address(0) && to != owner()) {
            require(balanceOf(to) + value <= maxWalletAmount, "Exceeds maxWalletAmount");
        }

        super._update(from, to, value);
    }

    receive() external payable {}
}

