import "@openzeppelin/contracts@4.9.5/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts@4.9.5/token/ERC20/IERC20.sol";

/// @notice –õ–∏–Ω–µ–π–Ω—ã–π –≤–µ—Å—Ç–∏–Ω–≥: —Ç–æ–∫–µ–Ω—ã –≤—ã—Å–≤–æ–±–æ–∂–¥–∞—é—Ç—Å—è —Ä–∞–≤–Ω–æ–º–µ—Ä–Ω–æ –æ—Ç start –¥–æ end.
contract GADLinearVesting {
    using SafeERC20 for IERC20;

    IERC20  public immutable token;
    address public immutable beneficiary;
    uint64  public immutable start;            // –≤—Ä–µ–º—è –Ω–∞—á–∞–ª–∞ –≤–µ—Å—Ç–∏–Ω–≥–∞
    uint64  public immutable end;              // –≤—Ä–µ–º—è –æ–∫–æ–Ω—á–∞–Ω–∏—è –≤–µ—Å—Ç–∏–Ω–≥–∞
    uint256 public immutable totalAllocation;  // –≤—Å–µ–≥–æ –∞–ª–ª–æ—Ü–∏—Ä–æ–≤–∞–Ω–æ –≤ –≤–µ—Å—Ç–∏–Ω–≥–µ
    uint256 public released;                   // —Å–∫–æ–ª—å–∫–æ —É–∂–µ –≤—ã–¥–∞–Ω–æ

    event Released(address indexed to, uint256 amount);

    constructor(
        IERC20 _token,
        address _beneficiary,
        uint64 _start,
        uint64 _end,
        uint256 _totalAllocation
    ) {
        require(address(_token) != address(0), "token=0");
        require(_beneficiary != address(0), "beneficiary=0");
        require(_start < _end, "start>=end");
        require(_totalAllocation > 0, "alloc=0");

        token = _token;
        beneficiary = _beneficiary;
        start = _start;
        end = _end;
        totalAllocation = _totalAllocation;
    }

    /// @notice –°–∫–æ–ª—å–∫–æ –¥–æ—Å—Ç—É–ø–Ω–æ –∫ –≤—ã–¥–∞—á–µ –Ω–∞ —Ç–µ–∫—É—â–∏–π –º–æ–º–µ–Ω—Ç
    function claimable() public view returns (uint256) {
        return vestedAmount(uint64(block.timestamp)) - released;
    }

    /// @notice –§–æ—Ä–º—É–ª–∞ –ª–∏–Ω–µ–π–Ω–æ–≥–æ –≤–µ—Å—Ç–∏–Ω–≥–∞
    function vestedAmount(uint64 timestamp) public view returns (uint256) {
        if (timestamp <= start) return 0;
        if (timestamp >= end) return totalAllocation;
        uint256 elapsed = uint256(timestamp - start);
        uint256 duration = uint256(end - start);
        return (totalAllocation * elapsed) / duration;
    }

    /// @notice –í—ã–¥–∞—Ç—å –¥–æ—Å—Ç—É–ø–Ω—É—é —á–∞—Å—Ç—å –±–µ–Ω–µ—Ñ–∏—Ü–∏–∞—Ä—É
    function release() external {
        uint256 amount = claimable();
        require(amount > 0, "nothing to release");
        released += amount;
        token.safeTransfer(beneficiary, amount);
        emit Released(beneficiary, amount);
    }
}

/// @notice GAD coin: 10 —Ç—Ä–ª–Ω —Ñ–∏–∫—Å, burn, 50% —Å—Ä–∞–∑—É, 50% –ª–∏–Ω–µ–π–Ω–æ –∑–∞ 180 –¥–Ω–µ–π.
contract GADCoin is ERC20, ERC20Burnable {
    // 10_000_000_000_000 * 10^18
    uint256 public constant MAX_SUPPLY = 10_000_000_000_000 * 10 ** 18;

    // –∞–¥—Ä–µ—Å —Å–æ–∑–¥–∞–Ω–Ω–æ–≥–æ –≤–µ—Å—Ç–∏–Ω–≥–∞ (–¥–ª—è –ø—Ä–æ–∑—Ä–∞—á–Ω–æ—Å—Ç–∏)
    GADLinearVesting public immutable vesting;

    event Vested(address indexed vesting, uint256 amount, uint64 start, uint64 end);

    /// @param immediateRecipient –∫—Ç–æ —Å—Ä–∞–∑—É –ø–æ–ª—É—á–∞–µ—Ç 50% (0x0 => deployer)
    /// @param vestingBeneficiary –∫—Ç–æ –±—É–¥–µ—Ç –ø–æ–ª—É—á–∞—Ç—å –ª–∏–Ω–µ–π–Ω—É—é —Ä–∞–∑–±–ª–æ–∫–∏—Ä–æ–≤–∫—É (0x0 => immediateRecipient)
    constructor(address immediateRecipient, address vestingBeneficiary)
        ERC20("GAD coin", "GAD")
    {
        address liquid = (immediateRecipient == address(0)) ? msg.sender : immediateRecipient;
        address bene   = (vestingBeneficiary == address(0)) ? liquid       : vestingBeneficiary;

        uint256 half = MAX_SUPPLY / 2;

        // —Å–æ–∑–¥–∞—ë–º –≤–µ—Å—Ç–∏–Ω–≥: —Å—Ç–∞—Ä—Ç —Å–µ–π—á–∞—Å, –∫–æ–Ω–µ—Ü —á–µ—Ä–µ–∑ 180 –¥–Ω–µ–π
        uint64 start = uint64(block.timestamp);
        uint64 end   = uint64(block.timestamp + 180 days);

        GADLinearVesting v = new GADLinearVesting(
            IERC20(address(this)),
            bene,
            start,
            end,
            half
        );
        vesting = v;

        // –º–∏–Ω—Ç–∏–º: 50% —Å—Ä–∞–∑—É, 50% ‚Äî –Ω–∞ –∞–¥—Ä–µ—Å –≤–µ—Å—Ç–∏–Ω–≥–∞
        _mint(liquid, half);
        _mint(address(v), half);

        emit Vested(address(v), half, start, end);
    }
}

